mapper.select:
SELECT

FROM bms_dispatch_order_data a
WHERE a.id = #{id}

SELECT

FROM bms_dispatch_order_data a

    a.del_flag = #{DEL_FLAG_NORMAL}
    AND a.org_id = #{orgId}
    ${dataScope}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM bms_dispatch_order_data a

    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE bms_dispatch_order_data
SET del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.delete:
DELETE FROM bms_dispatch_order_data WHERE
order_date >= #{fmDate}
AND order_date <= #{toDate}

    AND data_sources = #{dataSources}


    AND org_id = #{orgId}

mapper.select:
SELECT
,

FROM bms_dispatch_order_data a


    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}

        AND a.order_date >= #{orderDateFm}


        AND a.order_date <= #{orderDateTo}


        AND a.dispatch_time >= #{dispatchDateFm}


        AND a.dispatch_time <= #{dispatchDateTo}


        AND a.order_no LIKE concat(#{orderNo}, '%')


        AND a.order_type = #{orderType}


        AND a.carrier_code LIKE concat(#{carrierCode}, '%')


        AND a.carrier_name LIKE concat(#{carrierName}, '%')


        AND a.tran_type = #{tranType}


        AND a.car_type = #{carType}


        AND a.vehicle_no LIKE concat(#{vehicleNo}, '%')


        AND a.driver_name LIKE concat(#{driverName}, '%')




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT ,
FROM bms_dispatch_order_data a
WHERE a.id = #{id}
