	${dataScope}
	AND a.org_id = #{orgId}

		AND a.customer_no = #{customerNo}


		AND a.owner_code = #{ownerCode}


		AND a.sku_code = #{skuCode}


		AND a.channel = #{channel}


		AND a.price_type = #{priceType}




		ORDER BY ${page.orderBy}


