SELECT
o.ebcu_name_cn AS "ownerName",
b.ebcu_name_cn AS "customerName",
c.sku_name AS "skuName",
c.spec AS "spec",

FROM om_item_price a
LEFT JOIN om_customer b ON a.customer_no = b.ebcu_customer_no AND a.org_id = b.org_id
LEFT JOIN om_customer o ON a.owner_code = o.ebcu_customer_no AND a.org_id = o.org_id
LEFT JOIN om_item c ON a.owner_code = c.owner_code AND a.sku_code = c.sku_code AND  a.org_id = c.org_id

	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.id = #{id}


		AND a.owner_code = #{ownerCode}


		AND a.customer_no = #{customerNo}


		AND a.sku_code = #{skuCode}


		AND a.channel = #{channel}


		AND a.price_type = #{priceType}


		AND a.effective_time BETWEEN #{beginEffectiveTime} AND #{endEffectiveTime}


		AND a.expired_time BETWEEN #{beginExpirationTime} AND #{endExpirationTime}


		AND a.org_id = #{orgId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
o.ebcu_name_cn AS "ownerName",
b.ebcu_name_cn AS "customerName",
c.sku_name AS "skuName",
c.spec AS "spec",

FROM om_item_price a
LEFT JOIN om_customer b ON a.customer_no = b.ebcu_customer_no AND a.org_id = b.org_id
LEFT JOIN om_customer o ON a.owner_code = o.ebcu_customer_no AND a.org_id = o.org_id
LEFT JOIN om_item c ON a.owner_code = c.owner_code AND a.sku_code = c.sku_code AND a.org_id = c.org_id

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND (a.effective_time <= #{expirationTime} AND a.expired_time >= #{effectiveTime})
	AND (a.customer_no = #{customerNo} or a.customer_no = '无')
	AND a.sku_code = #{skuCode}
	AND a.channel = #{channel}
	AND a.price_type = #{priceType}
	AND a.org_id = #{orgId}

SELECT
b.ebcu_name_cn AS "customerName",
c.sku_name AS "skuName",
c.spec AS "spec",

FROM om_item_price a
LEFT JOIN om_customer b ON a.customer_no = b.ebcu_customer_no AND a.org_id = b.org_id
LEFT JOIN om_item c ON a.owner_code = c.owner_code AND a.sku_code = c.sku_code AND  a.org_id = c.org_id

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.org_id = #{orgId}
	AND a.is_enable = 'Y'
	AND (a.effective_time <= #{expirationTime} AND a.expired_time >= #{effectiveTime})
	${dataScope}

		AND a.owner_code = #{ownerCode}


		AND a.customer_no = #{customerNo}


		AND a.sku_code = #{skuCode}


		AND a.channel = #{channel}


		AND a.price_type = #{priceType}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT
b.ebcu_name_cn AS "customerName",
c.sku_name AS "skuName",
c.spec AS "spec",

FROM om_item_price a
LEFT JOIN om_customer b ON a.customer_no = b.ebcu_customer_no AND a.org_id = b.org_id
LEFT JOIN om_item c ON a.owner_code = c.owner_code AND a.sku_code = c.sku_code AND  a.org_id = c.org_id

	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}
	AND (a.effective_time <= #{expirationTime} AND a.expired_time >= #{effectiveTime})

		AND a.customer_no = #{customerNo}


		AND a.owner_code = #{ownerCode}


		AND a.sku_code = #{skuCode}


		AND a.channel = #{channel}


		AND a.price_type = #{priceType}


		AND a.org_id = #{orgId}

SELECT
	a.sku_code
FROM om_item c
LEFT JOIN om_item_price a ON a.owner_code = c.owner_code AND a.sku_code = c.sku_code AND a.org_id = c.org_id

	c.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}
	AND c.org_id = #{orgId}
	AND a.audit_status = '99'
	AND a.is_enable = 'Y'
	AND (a.effective_time <= #{expirationTime} AND a.expired_time >= #{effectiveTime})

		AND a.customer_no = #{customerNo}


		AND a.channel = #{channel}


		AND a.price_type = #{priceType}


		AND c.owner_code = #{ownerCode}


		AND c.sku_code = #{skuCode}

SELECT b.ebcu_name_cn AS "customerName", c.sku_name AS "skuName", c.spec AS "spec", a.customer_no AS "customerNo", c.sku_code AS "skuCode", a.channel AS "channel", a.price_type AS "priceType", a.purchase_multiple AS "purchaseMultiple", a.sale_multiple AS "saleMultiple", ifnull(a.unit, c.unit) AS "unit", ifnull(a.auxiliary_unit, c.auxiliary_unit) AS "auxiliaryUnit", a.discount AS "discount", a.tax_price AS "taxPrice", a.price AS "price", a.effective_time AS "effectiveTime", a.expired_time AS "expirationTime", a.is_allow_adjustment AS "isAllowAdjustment", c.org_id AS "orgId", a.convert_ratio AS "convertRatio", a.tax_rate AS "taxRate", a.logistics_muq_type AS "logisticsMuqType", c.owner_code AS "ownerCode" FROM om_item c LEFT JOIN om_item_price a ON a.owner_code = c.owner_code AND a.sku_code = c.sku_code AND a.org_id = c.org_id AND a.audit_status = '99' AND a.is_enable = 'Y'
AND (a.effective_time <= #{expirationTime} AND a.expired_time >= #{effectiveTime})
AND a.channel = #{channel}
AND a.price_type = #{priceType}
AND ( a.customer_no = #{customerNo} OR (a.customer_no = '无'
AND a.sku_code NOT IN
#{noSkuCode}
) )
LEFT JOIN om_customer b ON a.customer_no = b.ebcu_customer_no AND a.org_id = b.org_id
c.del_flag = #{DEL_FLAG_NORMAL} ${dataScope} AND c.org_id = #{orgId}
AND c.owner_code = #{ownerCode}
AND c.sku_code = #{skuCode}
ORDER BY ${page.orderBy}
ORDER BY c.create_date ASC
