mapper.select:
SELECT 

FROM sys_bms_route a

WHERE a.id = #{id}

SELECT 

FROM sys_bms_route a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.route_code = #{routeCode}


		AND a.route_name = #{routeName}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_bms_route a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



