mapper.select:
SELECT

FROM sys_oms_business_service_scope a

WHERE a.id = #{id}

SELECT

FROM sys_oms_business_service_scope a


    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}
    AND a.data_set = #{dataSet}

        AND a.group_code = #{groupCode}


        AND a.group_name = #{groupName}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

