mapper.select:
SELECT 

FROM sys_wms_rule_wv_group_detail a

WHERE a.id = #{id}

SELECT 

FROM sys_wms_rule_wv_group_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}
	AND a.data_set = #{dataSet}

		AND a.group_code = #{groupCode}


		AND a.header_id = #{headerId}




		ORDER BY ${page.orderBy}


		ORDER BY a.line_no

SELECT 

FROM sys_wms_rule_wv_group_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
