mapper.select:
SELECT

FROM tm_outlet_relation a

WHERE a.id = #{id}

SELECT

FROM tm_outlet_relation a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.parent_id = #{parent.id}


		AND a.parent_ids LIKE
		'%'||#{parentIds}||'%'
		'%'+#{parentIds}+'%'
		concat('%',#{parentIds},'%')


		AND a.code = #{code}


		AND a.name LIKE
		'%'||#{name}||'%'
		'%'+#{name}+'%'
		concat('%',#{name},'%')


		AND a.org_id = #{orgId}


ORDER BY a.sort ASC

SELECT

FROM tm_outlet_relation a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM tm_outlet_relation a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE tm_outlet_relation
SET parent_id  = #{parent.id},
	parent_ids = #{parentIds}
WHERE id = #{id}

mapper.select:
SELECT 
FROM tm_outlet_relation a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.parent_id = '0'

    AND a.org_id = #{orgId}

ORDER BY sort
