SELECT
t.transport_obj_name AS "dispatchOutletName",
tto.transport_obj_name AS "carrierName",
td.name AS "driverName",
so.name AS "orgName",
tdc.name AS "copilotName",
sas.name AS "startAreaName",
sae.name AS "endAreaName",
updater.name AS "updateBy.name",

FROM tm_dispatch_order_header a
LEFT JOIN tm_transport_obj t on a.dispatch_outlet_code = t.transport_obj_code AND a.base_org_id = t.org_id
LEFT JOIN tm_transport_obj tto on tto.transport_obj_code = a.carrier_code and tto.org_id = a.base_org_id
LEFT JOIN tm_driver td on td.code = a.driver and td.org_id = a.base_org_id
LEFT JOIN tm_driver tdc on tdc.code = a.copilot and tdc.org_id = a.base_org_id
LEFT JOIN sys_office so ON a.org_id = so.id
LEFT JOIN sys_area sas ON a.start_area_id = sas.id
LEFT JOIN sys_area sae ON a.end_area_id = sae.id
LEFT JOIN sys_user updater ON a.update_by = updater.id

    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}

        AND a.dispatch_no LIKE concat('%', #{dispatchNo}, '%')


        AND a.dispatch_status = #{dispatchStatus}


        AND a.dispatch_time >= #{dispatchTimeFm}


        AND a.dispatch_time <= #{dispatchTimeTo}


        AND a.dispatch_type = #{dispatchType}


        AND a.carrier_code = #{carrierCode}


        AND a.car_no LIKE concat('%', #{carNo}, '%')


        AND a.start_area_id = #{startAreaId}


        AND a.end_area_id = #{endAreaId}


        AND a.account = #{account}


        AND a.dispatch_plan_no = #{dispatchPlanNo}


        AND a.driver = #{driver}


        AND a.dispatch_no IN

            #{item}



        AND a.feed_back_status = #{feedBackStatus}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT
t.transport_obj_name AS "dispatchOutletName",
tto.transport_obj_name AS "carrierName",
td.name AS "driverName",
so.name AS "orgName",
tdc.name AS "copilotName",
sas.name AS "startAreaName",
sae.name AS "endAreaName",

FROM tm_dispatch_order_header a
LEFT JOIN tm_transport_obj t on a.dispatch_outlet_code = t.transport_obj_code AND a.base_org_id = t.org_id
LEFT JOIN tm_transport_obj tto on tto.transport_obj_code = a.carrier_code and tto.org_id = a.base_org_id
LEFT JOIN tm_driver td on td.code = a.driver and td.org_id = a.base_org_id
LEFT JOIN tm_driver tdc on tdc.code = a.copilot and tdc.org_id = a.base_org_id
LEFT JOIN sys_office so ON a.org_id = so.id
LEFT JOIN sys_area sas ON a.start_area_id = sas.id
LEFT JOIN sys_area sae ON a.end_area_id = sae.id

    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}

        AND a.dispatch_no LIKE concat('%', #{dispatchNo}, '%')


        AND a.org_id = #{orgId}


        AND a.dispatch_status = #{dispatchStatus}


        AND a.dispatch_time >= #{dispatchTimeFm}


        AND a.dispatch_time <= #{dispatchTimeTo}


        AND a.dispatch_type = #{dispatchType}


        AND a.carrier_code = #{carrierCode}


        AND a.car_no LIKE concat('%', #{carNo}, '%')


        AND a.account = #{account}


        AND a.dispatch_plan_no = #{dispatchPlanNo}


        AND a.dispatch_no IN

            #{item}



ORDER BY a.update_date DESC

SELECT a.label_no AS "labelNo", a.sku_code AS "skuCode", ti.sku_name AS "skuName", a.dispatch_no AS "dispatchNo", a.transport_no AS "transportNo", a.customer_no AS "customerNo", b.principal_code AS "principalCode", c.transport_obj_name AS "principalName", b.customer_code AS "customerCode", d.transport_obj_name AS "customerName", b.ship_code AS "shipCode", f.transport_obj_name AS "shipName", b.shipper AS "shipper", b.shipper_tel AS "shipperTel", b.ship_address AS "shipAddress", b.consignee_code AS "consigneeCode", e.transport_obj_name AS "consigneeName", b.consignee AS "consignee", b.consignee_tel AS "consigneeTel", b.consignee_address AS "consigneeAddress", a.org_id AS "orgId", a.base_org_id AS "baseOrgId", a.cabin AS "cabin", a.qty AS "qty", a.weight AS "weight", a.cubic AS "cubic", ttol.def1 AS "def1", ttol.def2 AS "def2" FROM tm_dispatch_order_label a LEFT JOIN tm_transport_order_header b ON a.transport_no = b.transport_no AND a.base_org_id = b.base_org_id LEFT JOIN tm_transport_obj c ON b.principal_code = c.transport_obj_code AND b.base_org_id = c.org_id LEFT JOIN tm_transport_obj d ON b.customer_code = d.transport_obj_code AND b.base_org_id = d.org_id LEFT JOIN tm_transport_obj e ON e.transport_obj_code = b.consignee_code AND e.org_id = b.base_org_id LEFT JOIN tm_transport_obj f ON f.transport_obj_code = b.ship_code AND f.org_id = b.base_org_id LEFT JOIN tm_item ti ON a.owner_code = ti.owner_code AND a.sku_code = ti.sku_code AND a.base_org_id = ti.org_id LEFT JOIN tm_transport_order_label ttol ON a.transport_no = ttol.transport_no AND a.label_no = ttol.label_no AND a.base_org_id = ttol.base_org_id
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.dispatch_no = #{dispatchNo}
AND a.transport_no = #{transportNo}
AND a.dispatch_site_outlet_code = #{dispatchSiteOutletCode}
AND a.org_id = #{orgId}
AND a.label_no = #{labelNo}
AND a.status = #{status}
AND a.receive_ship = #{receiveShip}
AND a.base_org_id = #{baseOrgId}
ORDER BY ${page.orderBy}
ORDER BY a.label_no ASC

SELECT a.transport_no AS "transportNo", a.customer_no AS "customerNo", b.order_time AS "orderTime", b.receive_outlet_code AS "receiveOutletCode", f.transport_obj_name AS "receiveOutletName", b.consignee_code AS "consigneeCode", c.transport_obj_name AS "consigneeName", b.consignee_city_id AS "consigneeCityId", b.consignee AS "consignee", b.consignee_tel AS "consigneeTel", b.consignee_address AS "consigneeAddress", b.ship_code AS "shipCode", e.transport_obj_name AS "shipName", b.ship_city_id AS "shipCityId", b.shipper AS "shipper", b.shipper_tel AS "shipperTel", b.ship_address AS "shipAddress", SUM(IFNULL(a.qty, 0)) AS "orderDelivery.totalEaQty", SUM(IFNULL(a.weight, 0)) AS "orderDelivery.totalWeight", SUM(IFNULL(a.cubic, 0)) AS "orderDelivery.totalCubic" FROM tm_dispatch_order_label a LEFT JOIN tm_transport_order_header b ON a.transport_no = b.transport_no AND a.base_org_id = b.base_org_id LEFT JOIN tm_transport_obj c ON c.transport_obj_code = b.consignee_code AND c.org_id = b.base_org_id LEFT JOIN tm_transport_obj e ON e.transport_obj_code = b.ship_code AND e.org_id = b.base_org_id LEFT JOIN tm_transport_obj f ON f.transport_obj_code = b.receive_outlet_code AND f.org_id = b.base_org_id
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.dispatch_no = #{dispatchNo}
AND a.transport_no = #{transportNo}
AND a.dispatch_site_outlet_code = #{dispatchSiteOutletCode}
AND a.receive_ship = #{receiveShip}
AND a.org_id = #{orgId}
AND a.base_org_id = #{baseOrgId}
GROUP BY a.transport_no
ORDER BY ${page.orderBy}
ORDER BY a.transport_no ASC

SELECT
,
b.transport_obj_name AS "outletName",
b.contact AS "outletContact",
b.phone AS "outletPhone",
b.address AS "outletAddress",
b.transport_obj_type AS "outletType"
FROM tm_dispatch_order_site a
LEFT JOIN tm_transport_obj b ON b.transport_obj_code = a.outlet_code AND b.org_id = a.base_org_id

    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}

        AND a.dispatch_no = #{dispatchNo}


        AND a.org_id = #{orgId}


        AND a.base_org_id = #{baseOrgId}




        ORDER BY ${page.orderBy}


        ORDER BY a.dispatch_seq

SELECT
b.principal_code     AS "principalCode",
c.transport_obj_name AS "principalName",
b.customer_code      AS "customerCode",
c.transport_obj_name AS "customerName",
b.shipper            AS "shipper",
b.shipper_tel        AS "shipperTel",
b.ship_address       AS "shipAddress",
b.consignee          AS "consignee",
b.consignee_tel      AS "consigneeTel",
b.consignee_address  AS "consigneeAddress",

FROM tm_dispatch_order_label a
LEFT JOIN tm_transport_order_header b ON a.transport_no = b.transport_no AND a.base_org_id = b.base_org_id
LEFT JOIN tm_transport_obj c ON b.principal_code = c.transport_obj_code AND b.base_org_id = c.org_id
LEFT JOIN tm_transport_obj d ON b.customer_code = d.transport_obj_code AND b.base_org_id = d.org_id

    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}

        AND a.dispatch_no = #{dispatchNo}


        AND a.transport_no = #{transportNo}


        AND a.dispatch_site_outlet_code = #{dispatchSiteOutletCode}


        AND a.org_id = #{orgId}


        AND a.base_org_id = #{baseOrgId}


        AND a.label_no = #{labelNo}


        AND a.status = #{status}


        AND a.receive_ship = #{receiveShip}




        ORDER BY ${page.orderBy}


        ORDER BY a.label_no ASC

SELECT ttor.transport_no AS "transportNo", ttoh.customer_no AS "customerNo", ttor.label_no AS "labelNo", ttoh.principal_code AS "principalCode", tto1.transport_obj_name AS "principalName", ttor.owner_code AS "customerCode", tto2.transport_obj_name AS "customerName", ttor.sku_code AS "skuCode", ti.sku_name AS "skuName", ttor.now_outlet_code AS "nowOutletCode", tto3.transport_obj_name AS "nowOutletName", ttoh.ship_city_id AS "nowCityId", ttor.next_outlet_code AS "nextOutletCode", tto4.transport_obj_name AS "nextOutletName", ttoh.consignee_city_id AS "nextCityId", ttor.base_org_id AS "baseOrgId", ttol.qty AS "qty", ttol.weight AS "weight", ttol.cubic AS "cubic", ttoh.order_type AS "orderType" FROM tm_transport_order_route ttor LEFT JOIN tm_transport_order_header ttoh ON ttor.transport_no = ttoh.transport_no AND ttor.base_org_id = ttoh.base_org_id LEFT JOIN tm_transport_order_label ttol ON ttoh.transport_no = ttol.transport_no AND ttor.label_no = ttol.label_no AND ttoh.org_id = ttol.org_id LEFT JOIN tm_transport_order_delivery ttod ON ttoh.transport_no = ttod.transport_no AND ttoh.base_org_id = ttod.base_org_id LEFT JOIN tm_transport_obj tto1 ON ttoh.principal_code = tto1.transport_obj_code AND ttoh.base_org_id = tto1.org_id LEFT JOIN tm_transport_obj tto2 ON ttor.owner_code = tto2.transport_obj_code AND ttoh.base_org_id = tto2.org_id LEFT JOIN tm_transport_obj tto3 ON ttor.now_outlet_code = tto3.transport_obj_code AND ttor.base_org_id = tto3.org_id LEFT JOIN tm_transport_obj tto4 ON ttor.next_outlet_code = tto4.transport_obj_code AND ttor.base_org_id = tto4.org_id LEFT JOIN tm_item ti ON ttor.owner_code = ti.owner_code AND ttor.sku_code = ti.sku_code AND ttor.base_org_id = ti.org_id WHERE ttor.pre_alloc_dispatch_no = '*' AND ttor.base_org_id = #{baseOrgId}
AND ttor.transport_no = #{transportNo}
AND ttoh.customer_no = #{customerNo}
AND ttoh.principal_code = #{principalCode}
AND ttor.owner_code = #{customerCode}
AND ttoh.consignee_code = #{consigneeCode}
AND ttoh.order_time >= #{transportOrderTimeFm}
AND ttoh.order_time <= #{transportOrderTimeTo}
AND (ttod.carrier_code = #{carrierCode} OR ttod.carrier_code IS NULL OR ttod.carrier_code = '')
AND ttoh.order_type = #{orderType}

SELECT tdol.dispatch_no AS "dispatchNo", tdol.transport_no AS "transportNo", tdol.customer_no AS "customerNo", tdol.label_no AS "labelNo", ttoh.principal_code AS "principalCode", tto2.transport_obj_name AS "principalName", tdol.owner_code AS "customerCode", tto1.transport_obj_name AS "customerName", tdol.sku_code AS "skuCode", ti.sku_name AS "skuName", tdol.dispatch_site_outlet_code AS "nowOutletCode", tto3.transport_obj_name AS "nowOutletName", ttoh.ship_city_id AS "nowCityId", ttor.next_outlet_code AS "nextOutletCode", tto4.transport_obj_name AS "nextOutletName", ttoh.consignee_city_id AS "nextCityId", tdol.base_org_id AS "baseOrgId", tdol.qty AS "qty", tdol.weight AS "weight", tdol.cubic AS "cubic", ttoh.order_type AS "orderType" FROM tm_dispatch_order_label tdol LEFT JOIN tm_transport_order_header ttoh ON tdol.transport_no = ttoh.transport_no AND tdol.base_org_id = ttoh.base_org_id LEFT JOIN tm_transport_order_route ttor ON tdol.transport_no = ttor.transport_no AND tdol.label_no = ttor.label_no AND tdol.base_org_id = ttor.base_org_id LEFT JOIN tm_transport_order_delivery ttod ON ttoh.transport_no = ttod.transport_no AND ttoh.base_org_id = ttod.base_org_id LEFT JOIN tm_transport_obj tto1 ON tdol.owner_code = tto1.transport_obj_code AND tdol.base_org_id = tto1.org_id LEFT JOIN tm_transport_obj tto2 ON ttoh.principal_code = tto2.transport_obj_code AND ttoh.base_org_id = tto2.org_id LEFT JOIN tm_item ti ON tdol.owner_code = ti.owner_code AND tdol.sku_code = ti.sku_code AND tdol.base_org_id = ti.org_id LEFT JOIN tm_transport_obj tto3 ON tdol.dispatch_site_outlet_code = tto3.transport_obj_code AND tdol.base_org_id = tto3.org_id LEFT JOIN tm_transport_obj tto4 ON ttor.next_outlet_code = tto4.transport_obj_code AND ttor.base_org_id = tto4.org_id WHERE tdol.receive_ship = 'R' AND NOT EXISTS(SELECT 1 FROM tm_dispatch_order_label a WHERE tdol.dispatch_no = a.dispatch_no AND tdol.label_no = a.label_no AND tdol.org_id = a.org_id AND a.receive_ship = 'S') AND tdol.dispatch_no = #{dispatchNo} AND tdol.dispatch_site_outlet_code != #{dispatchSiteOutletCode} AND tdol.org_id = #{orgId}
AND tdol.transport_no = #{transportNo}
AND tdol.customer_no = #{customerNo}
AND ttoh.principal_code = #{principalCode}
AND tdol.owner_code = #{customerCode}
AND ttoh.consignee_code = #{consigneeCode}
AND ttoh.order_time >= #{transportOrderTimeFm}
AND ttoh.order_time <= #{transportOrderTimeTo}
AND ttoh.order_type = #{orderType}

SELECT a.transport_no AS "transportNo", a.customer_no AS "customerNo", b.customer_code AS "customerCode", c.transport_obj_name AS "customerName", b.consignee_code AS "consigneeCode", e.transport_obj_name AS "consigneeName", b.consignee_city_id AS "consigneeCityId", b.consignee_city_name AS "consigneeCity", b.consignee AS "consignee", b.consignee_tel AS "consigneeTel", b.consignee_address AS "consigneeAddress", d.total_ea_qty AS "totalQty", d.total_weight AS "totalWeight" FROM tm_dispatch_order_label a LEFT JOIN tm_transport_order_header b ON a.transport_no = b.transport_no AND a.base_org_id = b.base_org_id LEFT JOIN tm_transport_obj c ON c.transport_obj_code = b.customer_code AND c.org_id = b.base_org_id LEFT JOIN tm_transport_order_delivery d ON a.transport_no = d.transport_no AND a.org_id = d.org_id LEFT JOIN tm_transport_obj e ON e.transport_obj_code = b.consignee_code AND e.org_id = b.base_org_id
a.del_flag = #{DEL_FLAG_NORMAL} AND a.receive_ship = 'R' AND a.dispatch_no = #{dispatchNo} AND a.org_id = #{orgId} ${dataScope}
ORDER BY ${page.orderBy}
ORDER BY a.transport_no ASC
