SELECT

FROM tm_transport_order_track a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.transport_no = #{transportNo}


		AND a.customer_no = #{customerNo}


		AND a.label_no = #{labelNo}


		AND a.org_id = #{orgId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

