mapper.select:
SELECT

FROM cd_tracking_info a
WHERE a.id = #{id}

SELECT

FROM cd_tracking_info a

    a.del_flag = #{DEL_FLAG_NORMAL}
    AND a.org_id = #{orgId}
    ${dataScope}

        AND a.type = #{type}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM cd_tracking_info a

    a.del_flag = #{DEL_FLAG_NORMAL}
    AND a.org_id = #{orgId}
    ${dataScope}

        AND a.type = #{type}


        AND a.description like concat(#{description}, '%')




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM cd_tracking_info a

    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE cd_tracking_info SET
    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
