mapper.select:
SELECT 

FROM cd_wh_loc a

WHERE a.id = #{id}

		SELECT 

		FROM cd_wh_loc a


			a.del_flag = #{DEL_FLAG_NORMAL}
            AND a.org_id = #{orgId}
			${dataScope}

				AND a.loc_code = #{locCode}


				AND a.zone_code = #{zoneCode}


				AND a.status = #{status}


				AND a.is_enable = #{isEnable}


				AND a.category = #{category}


				AND a.loc_use_type = #{locUseType}


				AND a.pa_seq = #{paSeq}


				AND a.pk_seq = #{pkSeq}


				AND a.abc = #{abc}




				ORDER BY ${page.orderBy}


				ORDER BY a.loc_code

SELECT

FROM cd_wh_loc a


	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.org_id = #{orgId}
	${dataScope}

		AND a.loc_code like concat('%', #{locCode}, '%')


		AND a.zone_code like concat('%', #{zoneCode}, '%')


		AND a.status = #{status}


		AND a.is_enable = #{isEnable}


		AND a.category = #{category}


		AND a.loc_use_type = #{locUseType}


		AND a.pa_seq = #{paSeq}


		AND a.pk_seq = #{pkSeq}


		AND a.abc = #{abc}


		AND (1 = 2
		OR a.loc_code like concat('%', #{codeAndName}, '%')
		OR a.zone_code like concat('%', #{codeAndName}, '%')
		)




		ORDER BY ${page.orderBy}


		ORDER BY a.loc_code

SELECT 

FROM cd_wh_loc a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cd_wh_loc SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT

FROM cd_wh_loc a

WHERE 1=1
AND a.org_id = #{orgId}
AND a.loc_code IN

	#{locCode}

SELECT

FROM cd_wh_loc a

WHERE a.loc_code = #{locCode} and a.org_id = #{orgId}
