mapper.select:
SELECT 

FROM wm_asn_detail a

WHERE a.id = #{id}

		SELECT 

		FROM wm_asn_detail a


			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.org_id = #{orgId}


                AND a.head_id = #{headId}


				AND a.asn_no = #{asnNo}


				AND a.line_no = #{lineNo}


				AND a.logistic_no = #{logisticNo}


				AND a.logistic_line_no = #{logisticLineNo}


				AND a.po_no = #{poNo}


				AND a.po_line_no = #{poLineNo}


				AND a.status = #{status}


				AND a.owner_code = #{ownerCode}


				AND a.sku_code = #{skuCode}


				AND a.pack_code = #{packCode}

			${dataScope}



				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

SELECT
    ,
    cws.sku_name AS skuName,
    cws.quick_code AS quickCode,
    cws.shelf_life AS shelfLife,
    ebcu.ebcu_name_cn AS ownerName,
    cwp.cdpa_format AS packDesc,
    cwpr.cdpr_desc AS uomDesc,
    cwpr.cdpr_quantity AS uomQty,
    plqty.cdpr_quantity AS plQty,
    crqh.rule_name AS qcRuleName,
    cwqih.item_group_name AS itemGroupName,
    crph.rule_name AS paRuleName
FROM wm_asn_detail a
LEFT JOIN eb_customer ebcu ON ebcu.ebcu_customer_no = a.owner_code AND ebcu.org_id = a.org_id
LEFT JOIN cd_wh_sku cws ON a.owner_code = cws.owner_code AND a.sku_code = cws.sku_code AND a.org_id = cws.org_id
LEFT JOIN cd_wh_package cwp ON a.pack_code = cwp.cdpa_code AND a.org_id = cwp.org_id
LEFT JOIN cd_wh_package_relation cwpr ON cwp.pm_code = cwpr.cdpr_cdpa_pm_code AND cwp.org_id = cwpr.org_id AND cwpr.cdpr_unit_level = a.uom
LEFT JOIN cd_wh_package_relation plqty ON cwp.pm_code = plqty.cdpr_cdpa_pm_code AND plqty.org_id = cwp.org_id AND plqty.cdpr_unit_level = 'PL'
LEFT JOIN cd_rule_pa_header crph ON crph.rule_code = a.pa_rule AND crph.org_id = a.org_id
LEFT JOIN cd_rule_qc_header crqh ON crqh.rule_code = a.qc_rule AND crqh.org_id = a.org_id
LEFT JOIN cd_wh_qc_item_header cwqih ON cwqih.item_group_code = a.item_group_code AND cwqih.org_id = a.org_id

    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}

        AND a.head_id = #{headId}


        AND a.asn_no = #{asnNo}


        AND a.line_no = #{lineNo}


        AND a.logistic_no = #{logisticNo}


        AND a.logistic_line_no = #{logisticLineNo}


        AND a.po_no = #{poNo}


        AND a.po_line_no = #{poLineNo}


        AND a.status = #{status}


        AND a.owner_code = #{ownerCode}


        AND a.sku_code = #{skuCode}


        AND a.pack_code = #{packCode}




        ORDER BY ${page.orderBy}


        ORDER BY a.line_no

SELECT 

FROM wm_asn_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wm_asn_detail SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 
FROM wm_asn_detail a WHERE a.ASN_NO = #{asnNo} AND a.ORG_ID = #{orgId}

    AND a.LINE_NO in

        #{lineNo}

SELECT 
FROM wm_asn_detail a
WHERE 1 = 1
  AND a.head_id = #{headId}
  AND a.IS_PALLETIZE = #{isPalletize}

SELECT DISTINCT wad.line_no FROM wm_asn_detail wad WHERE 1 = 1 AND NOT EXISTS ( SELECT 1 FROM wm_qc_sku wqs WHERE wqs.order_no = wad.asn_no AND wqs.order_line_no = wad.line_no AND wqs.org_id = wad.org_id
AND wqs.status = #{qcStatus} ) AND wad.head_id = #{asnId}
AND wad.line_no in
#{lineNo}

SELECT DISTINCT wad.line_no FROM wm_asn_detail wad LEFT JOIN wm_asn_header wah ON wah.asn_no = wad.asn_no AND wah.org_id = wad.org_id WHERE 1 = 1
AND wad.is_palletize = #{isPalletize}
AND wad.head_id = #{asnId}
AND wad.line_no IN
#{lineNo}
AND wad.status IN
#{lineStatus}
AND wah.status IN
#{asnStatus}
AND wah.hold_status IN
#{holdStatus}
AND wah.audit_status IN
#{auditStatus}
AND EXISTS(SELECT 1 FROM wm_asn_detail_receive wadr WHERE wadr.asn_no = wad.asn_no AND wadr.asn_line_no = wad.line_no AND wadr.org_id = wad.org_id AND wadr.plan_pa_loc IS NOT NULL AND wadr.plan_pa_loc != '')
AND NOT EXISTS(SELECT 1 FROM wm_asn_detail_receive wadr WHERE wadr.asn_no = wad.asn_no AND wadr.asn_line_no = wad.line_no AND wadr.org_id = wad.org_id AND wadr.plan_pa_loc IS NOT NULL AND wadr.plan_pa_loc != '')
AND EXISTS(SELECT 1 FROM wm_asn_detail_receive wadr WHERE wadr.asn_no = wad.asn_no AND wadr.asn_line_no = wad.line_no AND wadr.org_id = wad.org_id AND wadr.cd_Type IS NOT NULL AND wadr.cd_Type != '')
AND NOT EXISTS(SELECT 1 FROM wm_asn_detail_receive wadr WHERE wadr.asn_no = wad.asn_no AND wadr.asn_line_no = wad.line_no AND wadr.org_id = wad.org_id AND wadr.cd_Type IS NOT NULL AND wadr.cd_Type != '')
