mapper.select:
SELECT 

FROM wm_asn_header a

WHERE a.id = #{id}

SELECT 

FROM wm_asn_header a

	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.asn_no = #{asnNo}


		AND a.logistic_no = #{logisticNo}


		AND a.asn_type = #{asnType}


		 AND a.order_time >= #{beginOrderTime} 


		 AND a.order_time <= #{endOrderTime} 


		AND a.priority = #{priority}


		AND a.status = #{status}


		AND a.audit_status = #{auditStatus}


		AND a.audit_op = #{auditOp}


		AND a.audit_time = #{auditTime}


		AND a.hold_status = #{holdStatus}


		AND a.hold_op = #{holdOp}


		AND a.hold_time = #{holdTime}


		AND a.owner_code = #{ownerCode}


		AND a.supplier_code = #{supplierCode}


		AND a.carrier_code = #{carrierCode}


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date = #{updateDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

        SELECT
            ,
			owner.ebcu_name_cn AS ownerName,
			supplier.ebcu_name_cn AS supplierName,
			carrier.ebcu_name_cn AS carrierName,
			settle.ebcu_name_cn AS settleName,
            creater.name AS "createBy.name",
            updater.name AS "updateBy.name",
            so.name AS orgName
        FROM wm_asn_header a
		LEFT JOIN eb_customer owner ON a.owner_code = owner.ebcu_customer_no AND a.org_id = owner.org_id AND FIND_IN_SET('OWNER', owner.ebcu_type)
		LEFT JOIN eb_customer supplier ON a.supplier_code = supplier.ebcu_customer_no AND a.org_id = supplier.org_id AND FIND_IN_SET('SUPPLIER', supplier.ebcu_type)
		LEFT JOIN eb_customer carrier ON a.carrier_code = carrier.ebcu_customer_no AND a.org_id = carrier.org_id AND FIND_IN_SET('CARRIER', carrier.ebcu_type)
		LEFT JOIN eb_customer settle ON a.settle_code = settle.ebcu_customer_no AND a.org_id = settle.org_id AND FIND_IN_SET('SETTLEMENT', settle.ebcu_type)


            a.del_flag = #{DEL_FLAG_NORMAL}
            ${dataScope}

                AND a.asn_no like concat('%', #{asnNo}, '%')


                AND a.logistic_no like concat('%', #{logisticNo}, '%')


                AND a.asn_type = #{asnType}


				 AND a.order_time >= #{beginOrderTime} 


				 AND a.order_time <= #{endOrderTime} 


                AND a.priority = #{priority}


                AND a.status = #{status}


                AND a.audit_status = #{auditStatus}


                AND a.audit_op = #{auditOp}


                AND a.audit_time = #{auditTime}


                AND a.hold_status = #{holdStatus}


                AND a.hold_op = #{holdOp}


                AND a.hold_time = #{holdTime}


                AND a.owner_code = #{ownerCode}


                AND a.supplier_code = #{supplierCode}


                AND a.carrier_code = #{carrierCode}


                AND a.create_by = #{createBy.id}


                AND a.create_date = #{createDate}


                AND a.update_by = #{updateBy.id}


                AND a.update_date = #{updateDate}


				AND a.def1 like concat('%', #{def1}, '%')


				AND a.def2 like concat('%', #{def2}, '%')


				AND a.def3 like concat('%', #{def3}, '%')


				AND a.def4 like concat('%', #{def4}, '%')


				AND a.def5 like concat('%', #{def5}, '%')




                ORDER BY ${page.orderBy}


                ORDER BY a.update_date DESC

SELECT 

FROM wm_asn_header a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wm_asn_header SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
		SELECT 
        FROM wm_asn_header a
        WHERE a.asn_no = #{asnNo}
        AND a.org_id = #{orgId}

SELECT
FROM wm_asn_header a WHERE a.id IN
#{id}

SELECT wmd.asn_no AS asnNo, wmd.sku_code As skuCode, cdsk.sku_name AS skuName, wmd.owner_code AS ownerCode, ebcu1.ebcu_name_cn AS ownerName, wmh.supplier_code AS supplierCode, ebcu2.ebcu_name_cn AS supplierName, cdpa.cdpa_format AS packCode, cwpr.cdpr_quantity AS uomQty, sum(CEILING(wmdr.qty_plan_ea / cwpr.cdpr_quantity)) AS planBoxQty, sum(wmdr.qty_plan_ea) as planQty, sum(wmdr.qty_rcv_ea) AS rcvQty, wmdr.to_id AS toId, wmdr.plan_pa_loc AS planLoc, wmdr.to_loc AS toLoc, wmh.def4 AS customerNo, wmh.def5 AS def5, IFNULL(pl.cdpr_ti, '') AS ti, IFNULL(pl.cdpr_hi, '') AS hi, cdsk.spec AS def3, DATE_FORMAT(wmdr.lot_att01, '%Y-%m-%d') AS lotAtt01, su.name AS printer FROM wm_asn_detail wmd LEFT JOIN wm_asn_header wmh ON wmh.asn_no = wmd.asn_no AND wmh.org_id = wmd.org_id LEFT JOIN cd_wh_sku cdsk ON cdsk.sku_code = wmd.sku_code AND cdsk.owner_code = wmd.owner_code AND cdsk.org_id = wmd.org_id LEFT JOIN wm_asn_detail_receive wmdr ON wmdr.asn_no = wmd.asn_no AND wmdr.owner_code = wmd.owner_code AND wmdr.sku_code = wmd.sku_code AND wmdr.asn_line_no = wmd.line_no AND wmdr.org_id = wmd.org_id LEFT JOIN eb_customer ebcu1 ON ebcu1.ebcu_customer_no = wmd.owner_code AND ebcu1.org_id = wmd.org_id LEFT JOIN eb_customer ebcu2 ON ebcu2.ebcu_customer_no = wmh.supplier_code AND ebcu2.org_id = wmh.org_id LEFT JOIN cd_wh_package cdpa ON cdpa.org_id = wmdr.org_id AND cdpa.cdpa_code = wmdr.pack_code LEFT JOIN cd_wh_package_relation cwpr ON cdpa.pm_code = cwpr.cdpr_cdpa_pm_code AND cdpa.org_id = cwpr.org_id AND cwpr.cdpr_unit_level = 'CS' LEFT JOIN cd_wh_package_relation pl ON cdpa.pm_code = pl.cdpr_cdpa_pm_code AND cdpa.org_id = pl.org_id AND pl.cdpr_unit_level = 'PL' LEFT JOIN sys_user su ON su.id = wmh.create_by WHERE wmh.id IN
#{asnId}
GROUP BY wmd.asn_no, wmd.sku_code, wmd.owner_code, wmh.supplier_code, cdpa.cdpa_format, cwpr.cdpr_quantity, wmdr.to_id, wmdr.to_loc, wmdr.plan_pa_loc, pl.cdpr_ti, pl.cdpr_hi, DATE_FORMAT(wmdr.lot_att01, '%Y-%m-%d'), su.name ORDER BY wmd.asn_no, wmd.sku_code, wmdr.to_id

SELECT r.to_id AS traceId, r.owner_code As ownerCode, ebcu.ebcu_name_cn AS ownerName, r.sku_code As skuCode, cws.sku_name AS skuName, cws.spec AS skuDef3, r.pack_code AS packCode, cwp.cdpa_format AS packDesc, DATE_FORMAT(r.lot_att01, '%Y-%m-%d') AS lotAtt01, DATE_FORMAT(r.lot_att02, '%Y-%m-%d') AS lotAtt02, DATE_FORMAT(r.lot_att03, '%Y-%m-%d') AS lotAtt03, r.org_id AS orgId, so.`name` AS orgName, r.qty_plan_ea AS qtyPlanEa, cwpr.cdpr_quantity AS unitQty, FORMAT(r.qty_plan_ea / cwpr.cdpr_quantity, 1) AS qtyPlanBox, r.to_loc AS toLoc FROM wm_asn_detail_receive r LEFT JOIN wm_asn_detail d ON d.asn_no = r.asn_no AND d.line_no = r.asn_line_no AND d.org_id = r.org_id LEFT JOIn wm_asn_header h ON h.asn_no = r.asn_no AND h.org_id = r.org_id LEFT JOIN cd_wh_sku cws ON r.owner_code = cws.owner_code AND r.sku_code = cws.sku_code AND r.org_id = cws.org_id LEFT JOIN eb_customer ebcu ON r.owner_code = ebcu.ebcu_customer_no AND ebcu.ebcu_type like '%OWNER,%' AND ebcu.org_id = r.org_id LEFT JOIN cd_wh_package cwp ON cwp.cdpa_code = r.pack_code AND cwp.org_id = r.org_id LEFT JOIN cd_wh_package_relation cwpr ON cwp.pm_code = cwpr.cdpr_cdpa_pm_code AND cwp.org_id = cwpr.org_id AND cwpr.cdpr_unit = 'CS' LEFT JOIN sys_office so ON r.org_id = so.id WHERE d.is_palletize = 'Y' AND h.id IN
#{asnId}
ORDER BY h.asn_no, r.asn_line_no, r.line_no

SELECT wadr.asn_no AS asnNo, wadr.asn_line_no AS asnLineNo, wah.status AS status, wah.asn_type AS asnType, wadr.logistic_no AS logisticNo, wadr.logistic_line_no AS logisticLineNo, wadr.owner_code AS ownerCode, ec.ebcu_name_cn AS ownerName, wadr.sku_code AS skuCode, cws.sku_name AS skuName, cdpa.cdpa_format as packCde, wadr.uom AS uom, CEILING(wad.qty_asn_ea / cwpr.cdpr_quantity) AS qtyAsnUom, wad.qty_asn_ea AS qtyAsnEa, wadr.to_loc AS toLoc, wadr.to_id AS toId, CEILING(wadr.qty_rcv_ea / cwpr.cdpr_quantity) AS qtyRcvUom, wadr.qty_rcv_ea AS qtyRcvEa, wah.fm_eta AS fmEta, wah.to_eta AS toEta, wadr.rcv_time AS rcvTime, wadr.lot_att01 AS lotAtt01, wadr.lot_att02 AS lotAtt02, wadr.lot_att03 AS lotAtt03, wadr.lot_att04 AS lotAtt04, wadr.lot_att05 AS lotAtt05, wadr.lot_att06 AS lotAtt06, wadr.lot_att07 AS lotAtt07, wadr.lot_att08 AS lotAtt08, wadr.lot_att09 AS lotAtt09, wadr.lot_att10 AS lotAtt10, wadr.lot_att11 AS lotAtt11, wadr.lot_att12 AS lotAtt12, wah.org_id AS orgId, wah.def1 AS def1, wah.def2 AS def2, wah.def3 AS def3, wah.def4 AS def4, wah.def5 AS def5, so.name AS orgName, wah.order_time AS orderTime FROM wm_asn_header wah LEFT JOIN wm_asn_detail_receive wadr ON wah.asn_no = wadr.asn_no AND wah.org_id = wadr.org_id LEFT JOIN wm_asn_detail wad ON wadr.asn_no = wad.asn_no AND wadr.asn_line_no = wad.line_no AND wadr.org_id = wad.org_id LEFT JOIN cd_wh_sku cws ON wadr.owner_code = cws.owner_code AND wadr.sku_code = cws.sku_code AND wah.org_id = cws.org_id LEFT JOIN eb_customer ec ON wadr.owner_code = ec.ebcu_customer_no AND wadr.org_id = ec.org_id AND ec.ebcu_type LIKE concat('%OWNER%') LEFT JOIN cd_wh_package cdpa ON cdpa.org_id = wadr.org_id AND cdpa.cdpa_code = wadr.pack_code LEFT JOIN cd_wh_package_relation cwpr ON cdpa.pm_code = cwpr.cdpr_cdpa_pm_code AND cdpa.org_id = cwpr.org_id AND cwpr.cdpr_unit_level = 'CS' LEFT JOIN sys_office so ON wah.org_id = so.id
${dataScope}
AND wah.asn_no like concat('%', #{asnNo},'%')
AND wah.asn_type = #{asnType}
AND wah.status = #{status}
AND wadr.logistic_no like concat('%', #{logisticNo}, '%')
AND wadr.owner_code = #{ownerCode}
AND wadr.sku_code = #{skuCode}
AND wadr.rcv_time >= #{rcvTimeFrom}
AND wadr.rcv_time <= #{rcvTimeTo}
AND wah.order_time >= #{beginOrderTime}
AND wah.order_time <= #{endOrderTime}

SELECT wad.qty_asn_ea  AS qtyAsnEa,
	   wadr.qty_rcv_ea AS qtyRcvEa
FROM wm_asn_header wah
	 LEFT JOIN wm_asn_detail_receive wadr ON wah.asn_no = wadr.asn_no AND wah.org_id = wadr.org_id
	 LEFT JOIN wm_asn_detail wad ON wadr.asn_no = wad.asn_no AND wadr.asn_line_no = wad.line_no AND wadr.org_id = wad.org_id

	${dataScope}

		AND wah.asn_no like concat('%', #{asnNo},'%')


		AND wah.asn_type = #{asnType}


		AND wah.status = #{status}


		AND wadr.logistic_no like concat('%', #{logisticNo}, '%')


		AND wadr.owner_code = #{ownerCode}


		AND wadr.sku_code = #{skuCode}


		AND wadr.rcv_time >= #{rcvTimeFrom}


		AND wadr.rcv_time <= #{rcvTimeTo}

SELECT wmdt.id AS id, wmdt.asn_no AS asnNo, wmdt.line_no AS lineNo, wmdt.asn_line_no AS asnLineNo, wmdt.logistic_no AS logisticNo, wmdt.logistic_line_no AS logisticLineNo, wmdt.po_no AS poNo, wmdt.po_line_no AS poLineNo, wmdt.status AS status, wmad.is_palletize AS isPalletize, wmdt.owner_code AS ownerCode, ebcs.ebcu_name_cn AS ownerName, wmdt.sku_code AS skuCode, cdws.sku_name AS skuName, cdws.is_serial AS isSerial, wmdt.rcv_time AS rcvTime, wmdt.pack_code AS packCode, cdpa.cdpa_format AS packDesc, wmdt.uom AS uom, cdpr.cdpr_desc AS uomDesc, wmdt.qty_plan_ea AS qtyPlanEa, wmdt.plan_id AS planId, wmdt.qty_rcv_ea AS qtyRcvEa, wmdt.to_loc AS toLoc, wmdt.to_id AS toId, wmdt.price AS price, wmdt.pa_rule AS paRule, wmdt.pa_id AS paId, wmdt.lot_num AS lotNum, wmdt.lot_att01 AS lotAtt01, wmdt.lot_att02 AS lotAtt02, wmdt.lot_att03 AS lotAtt03, wmdt.lot_att04 AS lotAtt04, wmdt.lot_att05 AS lotAtt05, wmdt.lot_att06 AS lotAtt06, wmdt.lot_att07 AS lotAtt07, wmdt.lot_att08 AS lotAtt08, wmdt.lot_att09 AS lotAtt09, wmdt.lot_att10 AS lotAtt10, wmdt.lot_att11 AS lotAtt11, wmdt.lot_att12 AS lotAtt12, wmdt.org_id AS orgId, cdpr.cdpr_ti AS cdprTI, cdpr.cdpr_hi AS cdprHI FROM wm_asn_detail_receive wmdt LEFT JOIN wm_asn_header wmah ON wmah.asn_no = wmdt.asn_no AND wmah.org_id = wmdt.org_id LEFT JOIN wm_asn_detail wmad ON wmad.asn_no = wmdt.asn_no AND wmad.line_no = wmdt.asn_line_no AND wmad.org_id = wmdt.org_id LEFT JOIN cd_wh_sku cdws ON cdws.sku_code = wmad.sku_code AND cdws.owner_code = wmad.owner_code AND cdws.org_id = wmdt.org_id LEFT JOIN eb_customer ebcs ON ebcs.ebcu_customer_no = wmad.owner_code AND ebcs.org_id = wmdt.org_id LEFT JOIN cd_wh_package cdpa ON cdpa.cdpa_code = wmdt.pack_code AND cdpa.org_id = wmdt.org_id LEFT JOIN cd_wh_package_relation cdpr ON cdpr.Cdpr_Cdpa_pm_Code = cdpa.pm_code AND cdpr.org_id = wmdt.org_id AND cdpr.cdpr_unit_level = wmdt.uom WHERE wmah.audit_status = '99' AND wmdt.status = '00' AND cdws.is_serial = 'N' AND wmah.status >= '00' AND wmah.status < '20' AND wmad.status >= '00' AND wmad.status < '20' AND wmdt.asn_no = #{asnNo} AND wmdt.org_id = #{orgId}
AND wmdt.sku_code = #{skuCode}
AND wmdt.to_id = #{planId}
AND wmad.is_palletize = 'Y'
AND wmad.is_palletize = 'N'
ORDER BY wmdt.line_no, wmdt.plan_id, wmdt.sku_code

SELECT wah.asn_no AS asnNo, wah.def4 AS orderNo, wah.supplier_code AS supplierCode, ebcu1.ebcu_name_cn AS supplierName, wadr.asn_line_no as lineNo, wadr.sku_code As skuCode, cdsk.sku_name AS skuName, sum(wadr.qty_plan_ea) AS qtyPlanEa, sum(wadr.qty_rcv_ea) AS qtyRcvEa, cwpr.cdpr_quantity AS uomQty, sum(wadr.qty_rcv_ea / cwpr.cdpr_quantity) as qtyRcv, wadr.lot_att01 as lotAtt01, wadr.owner_code AS ownerCode FROM wm_asn_header wah LEFT JOIN wm_asn_detail_receive wadr ON wah.asn_no = wadr.asn_no AND wah.org_id = wadr.org_id LEFT JOIN eb_customer ebcu1 ON ebcu1.ebcu_customer_no = wah.supplier_code AND ebcu1.org_id = wah.org_id LEFT JOIN cd_wh_sku cdsk ON cdsk.sku_code = wadr.sku_code AND cdsk.owner_code = wadr.owner_code AND cdsk.org_id = wadr.org_id LEFT JOIN cd_wh_package cwp ON wadr.pack_code = cwp.cdpa_code AND wadr.org_id = cwp.org_id LEFT JOIN cd_wh_package_relation cwpr ON cwp.pm_code = cwpr.cdpr_cdpa_pm_code AND cwp.org_id = cwpr.org_id AND cwpr.cdpr_unit_level = wadr.uom WHERE wah.id IN
#{asnId}
GROUP BY wah.asn_no, wadr.asn_line_no, wadr.sku_code, cwpr.cdpr_quantity, wadr.lot_att01 ORDER BY wah.asn_no, wadr.asn_line_no
