SELECT

FROM wm_inv_lot_att a


	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.org_id = #{orgId}
	${dataScope}

		AND a.lot_num = #{lotNum}


		AND a.owner_code = #{ownerCode}


		AND a.sku_code = #{skuCode}


		AND a.lot_att01 = #{lotAtt01}


		AND a.lot_att02 = #{lotAtt02}


