SELECT , ebcu.ebcu_name_cn AS ownerName, cws.sku_name AS skuName , cwz.zone_name AS zoneName, cwa.area_name AS areaName, cwp.cdpa_format AS packDesc, cwpr.cdpr_desc AS uomDesc, cwpr.cdpr_quantity AS uomQuantity FROM wm_task_count a LEFT JOIN eb_customer ebcu ON a.owner_code = ebcu.ebcu_customer_no AND a.org_id = ebcu.org_id LEFT JOIN cd_wh_sku cws ON a.owner_code = cws.owner_code AND a.sku_code = cws.sku_code AND a.org_id = cws.org_id LEFT JOIN cd_wh_zone cwz ON cwz.zone_code = a.zone_code AND cwz.org_id = a.org_id LEFT JOIN cd_wh_area cwa ON cwa.area_code = a.area_code AND cwa.org_id = a.org_id LEFT JOIN cd_wh_package cwp ON cwp.cdpa_code = a.pack_code AND cwp.org_id = a.org_id LEFT JOIN cd_wh_package_relation cwpr ON cwpr.cdpr_cdpa_pm_code = cwp.pm_code AND cwpr.cdpr_unit_level = a.uom AND cwpr.org_id = a.org_id
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope} AND a.org_id = #{orgId}
AND a.header_id = #{headerId}
AND a.count_no = #{countNo}
AND a.count_id = #{countId}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

