mapper.update:
UPDATE wm_task_count SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT wmtc.id AS id, wmtc.header_id AS headerId, wmtc.count_id AS countId, wmtc.count_no AS countNo, wmtc.status AS status, wmch.count_method AS countMethod, wmch.count_mode AS countMode, wmtc.area_code AS areaCode, wmtc.zone_code AS zoneCode, wmtc.count_op AS countOp, wmch.parent_count_no AS parentCountNo, wmtc1.count_op as preCountOp, wmtc1.qty_count_ea AS preQtyCountEa, wmtc.owner_code AS ownerCode, ebcs.ebcu_name_cn as ownerName, wmtc.sku_code AS skuCode, cdws.sku_name as skuName, cdws.is_serial AS isSerial, wmtc.lot_num AS lotNum, wmtc.loc_code AS locCode, wmtc.trace_id AS traceId, wmtc.qty AS qty, wmtc.pack_code AS packCode, cdpa.cdpa_format AS packDesc, wmtc.uom AS uom, wmtc.qty_count_uom AS qtyCountUom, wmtc.qty_count_ea AS qtyCountEa, wmtc.qty_diff AS qtyDiff, wmtc.lot_att01 AS lotAtt01, wmtc.lot_att02 AS lotAtt02, wmtc.lot_att03 AS lotAtt03, wmtc.lot_att04 AS lotAtt04, wmtc.lot_att05 AS lotAtt05, wmtc.lot_att06 AS lotAtt06, wmtc.lot_att07 AS lotAtt07, wmtc.lot_att08 AS lotAtt08, wmtc.lot_att09 AS lotAtt09, wmtc.lot_att10 AS lotAtt10, wmtc.lot_att11 AS lotAtt11, wmtc.lot_att12 AS lotAtt12, wmtc.org_id AS orgId FROM wm_task_count wmtc LEFT JOIN wm_count_header wmch ON wmch.count_no = wmtc.count_no AND wmch.org_id = wmtc.org_id LEFT JOIN wm_task_count wmtc1 ON wmtc1.count_no = wmch.parent_count_no AND wmtc1.org_id = wmch.org_id AND wmtc1.owner_code = wmtc.owner_code AND wmtc1.sku_code = wmtc.sku_code AND wmtc1.lot_num = wmtc.lot_num AND wmtc1.loc_code = wmtc.loc_code AND wmtc1.trace_id = wmtc.trace_id LEFT JOIN cd_wh_sku cdws ON cdws.sku_code = wmtc.sku_code AND cdws.owner_code = wmtc.owner_code AND cdws.org_id = wmtc.org_id LEFT JOIN eb_customer ebcs ON ebcs.ebcu_customer_no = wmtc.owner_code AND ebcs.org_id = wmtc.org_id LEFT JOIN cd_wh_loc cdwl ON cdwl.loc_code = wmtc.loc_code AND cdwl.org_id = wmtc.org_id LEFT JOIN cd_wh_package cdpa ON cdpa.cdpa_code = wmtc.pack_code AND cdpa.org_id = wmtc.org_id WHERE 1 = 1 AND wmtc.status = '00' AND wmch.status > '00' AND wmch.status < '80' AND wmtc.org_id = #{orgId} AND wmtc.count_no = #{countNo}
AND cdwl.zone_code = #{zoneCode}
AND cdwl.lane = #{lane}
AND wmtc.loc_code = #{locCode}
ORDER BY wmtc.count_no, wmtc.count_id asc, cdwl.pk_seq
