mapper.select:
SELECT

FROM wm_ld_detail a

WHERE a.id = #{id}

SELECT

FROM wm_ld_detail a


    a.del_flag = #{DEL_FLAG_NORMAL}
    AND a.org_id = #{orgId}
    ${dataScope}

        AND a.ld_no = #{ldNo}


        AND a.line_no = #{lineNo}


        AND a.status = #{status}


        AND a.alloc_id = #{allocId}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT , wsa.wave_no AS waveNo, wsa.so_no AS soNo, wsa.line_no AS soLineNo, wsa.owner_code AS ownerCode, wsa.sku_code AS skuCode, wsa.lot_num AS lotNum, wsa.loc_code AS locCode, wsa.trace_id AS traceId, wsa.consignee_code AS consigneeCode, wsa.check_status AS checkStatus, wsa.pack_code AS packCode, wsa.uom AS uom, wsa.qty_uom AS qtyUom, wsa.qty_ea AS qtyEa, (wsa.qty_ea * IFNULL(cws.gross_weight, 0)) AS wsaGrossWeight, (wsa.qty_ea * IFNULL(cws.net_weight, 0)) AS wsaNetWeight, (wsa.qty_ea * IFNULL(cws.cubic, 0)) AS wsaCubic, wsa.qty_uom AS qtyPkUom, wsa.qty_ea AS qtyPkEa, wsa.to_loc AS toLoc, wsa.to_id AS toId, wsa.pick_op AS pickOp, wsa.pick_time AS pickTime, wsa.check_op AS checkOp, wsa.check_time AS checTime, wsa.pack_op AS packOp, wsa.pack_time AS packTime, wsa.ship_op AS shipOp, wsa.ship_time AS shipTime, wsa.print_num AS printUom, wsa.pick_no AS pickOp, wsa.remarks AS remarks, wsa.status AS allocStatus, wsa.tracking_no AS trackingNo, ec.ebcu_name_cn AS ownerName, cws.sku_name AS skuName, cdpa.cdpa_format AS packDesc, cdpr.cdpr_desc AS uomDesc, cdpr.cdpr_quantity AS uomQty from wm_ld_detail a LEFT JOIN wm_so_alloc wsa ON a.org_id = wsa.org_id AND a.alloc_id = wsa.alloc_id LEFT JOIN eb_customer ec ON ec.ebcu_customer_no = wsa.owner_code AND ec.org_id = wsa.org_id LEFT JOIN cd_wh_sku cws ON wsa.sku_code = cws.sku_code AND wsa.owner_code = cws.owner_code AND wsa.org_id = cws.org_id LEFT JOIN cd_wh_package cdpa ON wsa.pack_code = cdpa.cdpa_code AND wsa.org_id = cdpa.org_id LEFT JOIN cd_wh_package_relation cdpr ON cdpa.pm_code = cdpr.cdpr_cdpa_pm_code AND cdpa.org_id = cdpr.org_id AND wsa.uom = cdpr.cdpr_unit_level WHERE 1 = 1 AND a.org_id = #{orgId}
AND a.status = #{status}
AND a.ld_no = #{ldNo}
AND a.ld_no IN
#{ldNo}
AND wsa.so_no IN
#{soNo}
AND wsa.alloc_id IN
#{allocId}
AND wsa.to_id IN
#{toId}
ORDER BY a.line_no

SELECT distinct wsa.so_no AS soNo,
wsa.line_no AS lineNo,
wsa.org_id AS orgId
FROM wm_ld_detail wld
LEFT JOIN wm_so_alloc wsa ON wld.org_id = wsa.org_id AND wld.alloc_id = wsa.alloc_id
WHERE 1 = 1
AND wld.org_id = #{orgId}
AND wld.ld_no = #{ldNo}

    AND wsa.so_no = #{soNo}


    AND wsa.to_id = #{toId}


    AND wsa.alloc_id IN

        #{allocId}



    AND wsa.so_no IN

        #{soNo}



    AND wsa.to_id IN

        #{toId}

SELECT wsa.alloc_id AS allocId
FROM wm_so_alloc wsa
LEFT JOIN wm_ld_detail wld ON wsa.alloc_id = wld.alloc_id AND wsa.org_id = wld.org_id
WHERE 1=1
AND (wld.status IS NULL OR wld.status = '')
AND wsa.Check_Status IN ('90','99')
AND wsa.org_id = #{orgId}
AND wsa.status = #{status}

    AND wsa.so_no IN

        #{soNo}



    AND wsa.to_id IN

        #{toId}

SELECT

FROM wm_ld_detail a


    a.del_flag = #{DEL_FLAG_NORMAL}
    ${dataScope}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT COUNT(1)
FROM wm_ld_detail wld
LEFT JOIN wm_so_alloc wsa ON wld.alloc_id = wsa.alloc_id AND wld.org_id = wsa.org_id
WHERE 1 = 1
AND wld.org_id = #{orgId}

    AND wsa.to_id = #{toId}


    AND wsa.alloc_id = #{allocId}


    AND wsa.so_no = #{soNo}


    AND wld.status != '60' AND 'Y'= #{isFullDelivery}


    AND wsa.asn_no = #{asnNo}


    AND wsa.rcv_line_no= #{rcvLineNo}

LIMIT 1

SELECT DISTINCT wsa.so_no FROM wm_ld_detail wld
LEFT JOIN wm_so_alloc wsa ON wld.alloc_id = wsa.alloc_id AND wld.org_id = wsa.org_id
LEFT JOIN wm_so_detail wsd ON wsd.so_no = wsa.so_no AND wsd.line_no = wsa.line_no AND wsd.org_id = wsa.org_id
LEFT JOIN wm_so_header wsh ON wsh.so_no = wsd.so_no AND wsh.org_id = wsd.org_id
LEFT JOIN wm_wv_detail wwd ON wwd.so_no = wsh.so_no AND wwd.org_id = wsh.org_id
WHERE 1 = 1
AND wld.org_id = #{orgId}

    AND wld.status = #{status}


    AND wsa.so_no IN

        #{soNo}



    AND wsa.alloc_id IN

        #{allocId}



    AND wsa.wave_no IN

        #{waveNo}

SELECT DISTINCT wsa.to_id FROM wm_ld_detail wld
LEFT JOIN wm_so_alloc wsa ON wld.alloc_id = wsa.alloc_id AND wld.org_id = wsa.org_id
WHERE 1 = 1
AND wld.org_id = #{orgId}

    AND wld.status = #{status}


    AND wld.ld_no = #{ldNo}


    AND wsa.to_id IN

        #{toId}

SELECT DISTINCT wsa.alloc_id FROM wm_ld_detail wld
LEFT JOIN wm_so_alloc wsa ON wld.alloc_id = wsa.alloc_id AND wld.org_id = wsa.org_id
WHERE 1 = 1
AND wld.org_id = #{orgId}

    AND wld.status = #{status}


    AND wsa.alloc_id IN

        #{allocId}

mapper.update:
UPDATE wm_ld_detail SET
    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
select wsa.so_no AS soNo, wsa.owner_code AS ownerCode, ebcu2.ebcu_name_cn AS ownerName, wsh.so_type AS soType, wsh.status AS status, wsh.logistic_no AS logisticNo, wsh.fm_etd AS fmEtd, wsh.to_etd AS toEtd, wsh.order_time AS orderTime, wsh.consignee_code AS consigneeCode, wsh.consignee_name AS consigneeName, wsh.consignee_tel AS consigneeTel, wsh.consignee_addr AS consigneeAddr, wsh.contact_name AS contactName, wsh.contact_tel AS contactTel, wsh.contact_addr AS contactAddr, wsh.carrier_code AS carrierCode, ebcu.ebcu_name_cn AS carrierName, wsh.line AS line FROM wm_so_alloc wsa LEFT JOIN cd_wh_sku cws ON wsa.sku_code = cws.sku_code AND wsa.owner_code = cws.owner_code AND wsa.org_id = cws.org_id LEFT JOIN wm_inv_lot_att wila ON wsa.lot_num = wila.lot_num AND wsa.org_id = wila.org_id LEFT JOIN wm_so_header wsh ON wsa.so_no = wsh.so_no AND wsa.org_id = wsh.org_id LEFT JOIN eb_customer ebcu ON wsh.carrier_code = ebcu.ebcu_customer_no AND wsh.org_id = ebcu.org_id LEFT JOIN eb_customer ebcu2 ON cws.owner_code = ebcu2.ebcu_customer_no AND cws.org_id = ebcu2.org_id LEFT JOIN wm_ld_detail wld ON wsa.alloc_id = wld.alloc_id AND wsa.org_id = wld.org_id WHERE 1 = 1 AND wld.status IS NULL AND wsa.check_status IN ('90','99') AND wsh.status NOT IN ('99') AND wsa.org_id = #{orgId}
AND wsh.carrier_code IN
#{carrierCode}
AND wsh.consignee_code = #{consigneeCode}
AND wsh.consignee_name = #{consigneeName}
AND wsh.consignee_tel = #{consigneeTel}
AND wsh.consignee_addr = #{consigneeAddr}
AND wsh.contact_name = #{contactName}
AND wsh.contact_tel = #{contactTel}
AND wsh.contact_addr = #{contactAddr}
AND wsh.line = #{line}
AND wsh.logistic_no LIKE CONCAT(#{logisticNo}, '%')
AND wsh.so_no LIKE CONCAT(#{soNo}, '%')
AND wsh.owner_code IN
#{ownerCode}
AND wsh.so_type IN
#{soType}
AND wsa.status IN
#{status}
AND wsh.fm_etd >= #{fmEtd}
AND wsh.to_etd <= #{toEtd}
AND wsh.order_time >= #{fmOrderDate}
AND wsh.order_time <= #{toOrderDate}
GROUP BY wsa.so_no, wsa.owner_code, ebcu2.ebcu_name_cn, wsh.so_type, wsh.status, wsh.logistic_no, wsh.fm_etd, wsh.to_etd, wsh.order_time, wsh.consignee_code, wsh.consignee_name, wsh.consignee_tel, wsh.consignee_addr, wsh.contact_name, wsh.contact_tel, wsh.contact_addr, wsh.carrier_code, ebcu.ebcu_name_cn, wsh.line

SELECT wsa.so_no AS soNo, wsa.status AS status, wsa.alloc_id AS allocId, wsa.sku_code AS skuCode, cws.sku_name AS skuName, wsa.owner_code AS ownerCode, ebcu2.ebcu_name_cn AS ownerName, wsa.trace_id AS traceId, wsa.to_id AS toId, wsa.qty_ea AS qtyEa, wsa.qty_uom AS qtyUom, cdpa.cdpa_format AS packDesc, cdpr.cdpr_desc AS uomDesc, cdpr.cdpr_quantity AS uomQty, wsa.pack_code AS packCode, wsa.loc_code AS locCode, wsa.to_loc AS toLoc, wsa.lot_num AS lotNum, wsh.so_type AS soType, wsh.logistic_no AS logisticNo, wsh.fm_etd AS fmEtd, wsh.to_etd AS toEtd, wsh.order_time AS orderTime, wsh.consignee_code AS consigneeCode, wsh.consignee_name AS consigneeName, wsh.consignee_tel AS consigneeTel, wsh.consignee_addr AS consigneeAddr, wsh.contact_name AS contactName, wsh.contact_tel AS contactTel, wsh.contact_addr AS contactAddr, wsh.carrier_code AS carrierCode, ebcu.ebcu_name_cn AS carrierName, wsh.line AS line, wila.lot_att01 AS lotAtt01, wila.lot_att02 AS lotAtt02, wila.lot_att03 AS lotAtt03, wila.lot_att04 AS lotAtt04, wila.lot_att05 AS lotAtt05, wila.lot_att06 AS lotAtt06, wila.lot_att07 AS lotAtt07, wila.lot_att08 AS lotAtt08, wila.lot_att09 AS lotAtt09, wila.lot_att10 AS lotAtt10, wila.lot_att11 AS lotAtt11, wila.lot_att12 AS lotAtt12 FROM wm_so_alloc wsa LEFT JOIN cd_wh_sku cws ON wsa.sku_code = cws.sku_code AND wsa.owner_code = cws.owner_code AND wsa.org_id = cws.org_id LEFT JOIN cd_wh_package cdpa ON wsa.pack_code = cdpa.cdpa_code AND wsa.org_id = cdpa.org_id LEFT JOIN cd_wh_package_relation cdpr ON cdpa.pm_code = cdpr.cdpr_cdpa_pm_code AND cdpa.org_id = cdpr.org_id AND wsa.uom = cdpr.cdpr_unit_level LEFT JOIN wm_inv_lot_att wila ON wsa.lot_num = wila.lot_num AND wsa.org_id = wila.org_id LEFT JOIN wm_so_header wsh ON wsa.so_no = wsh.so_no AND wsa.org_id = wsh.org_id LEFT JOIN eb_customer ebcu ON wsh.carrier_code = ebcu.ebcu_customer_no AND wsh.org_id = ebcu.org_id LEFT JOIN eb_customer ebcu2 ON wsa.owner_code = ebcu2.ebcu_customer_no AND wsa.org_id = ebcu2.org_id LEFT JOIN wm_ld_detail wld ON wsa.alloc_id = wld.alloc_id AND wsa.org_id = wld.org_id WHERE 1 = 1 AND wld.status IS NULL AND wsa.Check_Status IN ('90','99') AND wsh.status NOT IN ('99') AND wsa.org_id = #{orgId}
AND wsa.sku_code = #{skuCode}
AND wsh.carrier_code = #{carrierCode}
AND wsh.consignee_code = #{consigneeCode}
AND wsh.consignee_name = #{consigneeName}
AND wsh.consignee_tel = #{consigneeTel}
AND wsh.consignee_addr = #{consigneeAddr}
AND wsh.contact_name = #{contactName}
AND wsh.contact_tel = #{contactTel}
AND wsh.contact_addr = #{contactAddr}
AND wsh.line = #{line}
AND wsh.logistic_no = #{logisticNo}
AND wsh.so_no LIKE CONCAT(#{soNo}, '%')
AND wsh.owner_code = #{ownerCode}
AND wsh.so_type = #{soType}
AND wsa.status = #{status}
AND wsh.fm_etd >= #{fmEtd}
AND wsh.to_etd <= #{toEtd}
AND wsh.order_time >= #{fmOrderDate}
AND wsh.order_time <= #{toOrderDate}
