AND wsh1.to_etd >= #{toEtdFm}
AND wsh1.to_etd <= #{toEtdTo}
GROUP BY wsd1.owner_code, wsd1.sku_code, wsd1.org_id) wsd2 ON wsd2.owner_code = wsd.owner_code AND wsd2.sku_code = wsd.sku_code AND wsd2.org_id = wsd.org_id WHERE 1 = 1 AND wsd.qty_so_ea - wsd.qty_prealloc_ea - wsd.qty_alloc_ea - wsd.qty_pk_ea - wsd.qty_ship_ea > 0 AND wsh.status != '90' AND wsh.status != '99' AND wsh.audit_status != '00' AND wsh.hold_status != '99' AND (wsh.intercept_status = '00' OR wsh.intercept_status = '90') AND cws.is_serial = 'N' AND wsd.org_id = #{orgId}
AND wsh.so_type IN
#{soType}
AND wsh.fm_etd >= #{fmEtdFm}
AND wsh.fm_etd <= #{fmEtdTo}
AND wsh.to_etd >= #{toEtdFm}
AND wsh.to_etd <= #{toEtdTo}
AND wsd2.so_line_count >= #{soLineNum}
AND wsd2.qty_so_ea_all >= #{qtySoEa}
AND wsd.owner_code = #{ownerCode}
AND wsd.sku_code = #{skuCode}
AND (wsd.cd_type IS NULL OR wsd.cd_type = '') AND 'Y' = #{isCd}
AND wsd.status = #{status}
AND wwd.wave_no = #{waveNo}
AND wsd.so_no = #{soNo}
AND wsd.line_no = #{lineNo}
AND (wsd.so_no, wsd.line_no) IN (${soAndLineNos})
AND wsd.cd_type IN
#{cdType}
ORDER BY wsh.to_etd is null, wsh.to_etd, wsh.priority is null || wsh.priority = '', wsh.priority, wsh.order_time is null, wsh.order_time, wsd.qty_so_ea DESC, wsd.so_no, wsd.line_no

