mapper.select:
SELECT 

FROM wm_qc_header a

WHERE a.id = #{id}

SELECT 

FROM wm_qc_header a


	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.org_id = #{orgId}

		AND a.qc_no = #{qcNo}


		AND a.qc_phase = #{qcPhase}


		AND a.status = #{status}


		AND a.audit_status = #{auditStatus}


		AND a.owner_code = #{ownerCode}


		AND a.order_type = #{orderType}


		AND a.order_no = #{orderNo}

	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
	,
	ebcu.ebcu_name_cn AS ownerName,
	creater.name AS "createBy.name",
	updater.name AS "updateBy.name",
	so.name AS orgName
FROM wm_qc_header a
LEFT JOIN eb_customer ebcu ON a.owner_code = ebcu.ebcu_customer_no AND a.org_id = ebcu.org_id AND ebcu.ebcu_type LIKE '%OWNER,%'


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}
	AND a.org_id = #{orgId}

		AND a.qc_no = #{qcNo}


		AND a.qc_phase = #{qcPhase}


		AND a.status = #{status}


		AND a.audit_status = #{auditStatus}


		AND a.owner_code = #{ownerCode}


		AND a.order_type = #{orderType}


		AND a.order_no = #{orderNo}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wm_qc_header a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wm_qc_header SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT wqh.id FROM wm_qc_header wqh WHERE 1 = 1 AND wqh.id IN
#{qcId}
AND wqh.status IN
#{status}
AND wqh.audit_status IN
#{auditStatus}
AND ( NOT EXISTS (SELECT 1 FROM wm_qc_sku wqs WHERE wqs.qc_no = wqh.qc_no AND wqs.org_id = wqh.org_id) OR EXISTS (SELECT 1 FROM wm_qc_sku wqs WHERE wqs.qc_no = wqh.qc_no AND wqs.org_id = wqh.org_id AND (wqs.pct_qua IS NULL OR wqs.pct_qua = '')) )
AND ( NOT EXISTS (SELECT 1 from wm_qc_sku wqs where wqs.qc_no = wqh.qc_no and wqs.org_id = wqh.org_id) OR EXISTS (SELECT 1 FROM wm_qc_sku wqs WHERE wqs.qc_no = wqh.qc_no AND wqs.org_id = wqh.org_id AND wqs.pct_qua IS NOT NULL AND wqs.pct_qua != '') )
