WHERE a.id = #{id}

SELECT 

FROM wm_task_rp a


	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.org_id = #{orgId}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT , ec.ebcu_name_cn AS ownerName, cdws.sku_name AS skuName, cwp.cdpa_format AS packDesc, cwpr_a.cdpr_quantity AS cdprQuantity, cwpr_a.cdpr_unit_level AS cdprUnitLevel, cwpr_a.cdpr_desc AS cdprDesc, wmil.qty AS qty, wmil.qty - wmil.qty_hold -wmil.qty_alloc - wmil.qty_pk - wmil.qty_pa_out - wmil.qty_rp_out - wmil.qty_mv_out AS qtyUse, wmil.qty_hold AS qtyHold, wmil.qty_alloc AS qtyAlloc, wmil.qty_pk AS qtyPk, wmil.qty_pa_out AS qtyPaOut, wmil.qty_rp_out AS qtyRpOut, wmil.qty_mv_out AS qtyMvOut, wmla.lot_att01 AS lotAtt01, wmla.lot_att02 AS lotAtt02, wmla.lot_att03 AS lotAtt03, wmla.lot_att04 AS lotAtt04, wmla.lot_att05 AS lotAtt05, wmla.lot_att06 AS lotAtt06, wmla.lot_att07 AS lotAtt07, wmla.lot_att08 AS lotAtt08, wmla.lot_att09 AS lotAtt09, wmla.lot_att10 AS lotAtt10, wmla.lot_att11 AS lotAtt11, wmla.lot_att12 AS lotAtt12, creater.name AS "createBy.name", updater.name AS "updateBy.name", so.name AS orgName FROM wm_task_rp a LEFT JOIN wm_inv_lot_loc wmil ON wmil.lot_num = a.lot_num AND wmil.trace_id = a.fm_id AND wmil.sku_code = a.sku_code AND wmil.owner_code = a.owner_code AND wmil.loc_code = a.fm_loc AND wmil.org_id = a.org_id LEFT JOIN wm_inv_lot_att wmla ON a.lot_num = wmla.lot_num AND a.org_id = wmla.org_id LEFT JOIN CD_WH_SKU cdws ON a.sku_code = cdws.sku_code AND a.owner_code = cdws.owner_code AND a.org_id = cdws.org_id LEFT JOIN EB_CUSTOMER ec ON a.owner_code = ec.ebcu_customer_no AND a.org_id = ec.org_id LEFT JOIN cd_wh_package cwp ON cwp.cdpa_code = a.pack_code AND cwp.org_id = a.org_id LEFT JOIN cd_wh_package_relation cwpr_a ON cwpr_a.cdpr_cdpa_pm_code = cwp.pm_code AND cwpr_a.cdpr_unit_level = a.uom AND cwpr_a.org_id = cwp.org_id LEFT JOIN cd_wh_package_relation cwpr_b ON cwpr_b.cdpr_cdpa_pm_code = cwp.pm_code AND cwpr_b.cdpr_unit_level = a.uom AND cwpr_b.org_id = cwp.org_id LEFT JOIN cd_wh_package_relation cwpr_c ON cwpr_c.cdpr_cdpa_pm_code = cwp.pm_code AND cwpr_c.cdpr_unit_level = a.uom AND cwpr_c.org_id = cwp.org_id
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.rp_id like concat('%', #{rpId}, '%')
AND a.status = #{status}
AND a.owner_code = #{ownerCode}
AND a.sku_code = #{skuCode}
AND a.fm_loc = #{fmLoc}
AND a.fm_id = #{fmId}
AND a.to_loc = #{toLoc}
AND a.to_id = #{toId}
ORDER BY ${page.orderBy}
ORDER BY a.rp_id DESC

